mapper.insert:
insert into auth_user (id, username, password, 
  salt, realname, avatar, 
  phone, email, sex, 
  locked, create_time, update_time
  )
values (#{id,jdbcType=VARCHAR}, #{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{salt,jdbcType=VARCHAR}, #{realname,jdbcType=VARCHAR}, #{avatar,jdbcType=VARCHAR}, 
  #{phone,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}, #{sex,jdbcType=TINYINT}, 
  #{locked,jdbcType=TINYINT}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
      SELECT * FROM auth_permission ap WHERE ap.status=1 and ap.id IN (select arp.permission_id from auth_role_permission arp where arp.role_id in (
				select aur.role_id role_id from auth_user_role aur where aur.user_id=#{authUserId,jdbcType=VARCHAR}
			)
			union
			select permission_id from auth_user_permission aup where aup.user_id=#{authUserId,jdbcType=VARCHAR} and aup.type=1
		)
		and ap.id not in (
			select permission_id from auth_user_permission aup1 where aup1.user_id=#{authUserId,jdbcType=VARCHAR} and aup1.type=-1
		) order by ap.orders asc

select
*
from auth_role ar where ar.id in (
select aur.role_id from auth_user_role aur where uur.user_id=#{authUserId,jdbcType=VARCHAR}
)
